    private async Task<bool> AddSubscriber()
    {
        var hasSubscribed = await Infrastructure.Services.BlogService.AddSubscriber(subscribeModel.Email);
        if (hasSubscribed)
        {
            subscribed = "Thank you for subscribing!";
